import {createContext} from 'react';
import {NodeTypes} from "./lib/types";

interface IState {
    isDialogOpen: boolean;
}

interface IContext {
    state: IState,
    dispatch: any
}


const appState: IState = {
    isDialogOpen: false
};


const FeaturesContext = createContext<IState>({
    ...appState
});

export default FeaturesContext;
