import {createContext} from 'react';

interface IState {
    isDialogOpen: boolean;
    query: string,
    selectedRowIds: any,
    dispatch: any
}

const appState: IState = {
    isDialogOpen: false,
    query: '',
    selectedRowIds: undefined,
    dispatch: () => {}
};


const FeaturesContext = createContext<IState>({
    ...appState
});

export default FeaturesContext;
