import React, {useCallback, useContext, useEffect, useState, useMemo} from 'react';
import FeaturesTable from '../FeaturesTable/FeaturesTable';
import {fetchFeatures, fetchModelMetadata} from '../../lib/mock-api';
import {ICrFlatNode, IModelMetadata} from "../../lib/types";
import FeaturesFilters from "../FeaturesFilters/FeaturesFilters";
import './FeaturesModal.scss';
import FeaturesContext from "../../FeaturesContext";


const FeaturesModal = () => {
    const [featuresFlatData, setFeaturesFlatData] = useState<ICrFlatNode[]>([]);
    const [featuresModelMetadata, setFeaturesModelMetadata] = useState<IModelMetadata>({labels: [], domains: []});
    const [featuresColumns] = useState([
        {
            Header: 'NAME',
            accessor: 'name',
        },
        {
            Header: 'VARIABLE ID',
            accessor: 'varId',
        },
        {
            Header: 'DESCRIPTION',
            accessor: 'description',
        },
        {
            Header: 'DOMAINS',
            accessor: 'domains',
            cell: () => (
                <select>
                    {featuresModelMetadata && featuresModelMetadata.domains.map((val, index) => {
                        return <option value={val} key={index}>{val}</option>;
                    })}
                </select>
            )
        },
        {
            Header: 'LABELS',
            accessor: 'labels',
            cell: () => (
                <select>
                    {featuresModelMetadata && featuresModelMetadata.labels.map((val, index) => {
                        return <option value={val} key={index}>{val}</option>;
                    })}
                </select>
            )
        },
        {
            Header: 'SOURCES',
            accessor: 'sources',
        },
        {
            Header: 'VALUE',
            accessor: 'value',
        }
    ]);
    const {dispatch, query} = useContext(FeaturesContext);

    const getFeaturesData = useCallback(() => {
        return fetchFeatures();
    }, []);

    const getModeMetadata = useCallback(() => {
        return fetchModelMetadata();
    }, []);

    useEffect(() => {
        getFeaturesData().then((res) => {
            if(res) {
                setFeaturesFlatData(res.map(item => {
                    return {
                        ...item,
                        ...item.attributes
                    }
                }));
            }
        }).catch((err) => {
            console.log(err)
        });

        getModeMetadata().then((res) => {
            if(res) {
                setFeaturesModelMetadata(res);
            }
        }).catch((err) => {
            console.log(err)
        });
    }, []);

    const filterFeaturesData = useMemo(() => {
        return featuresFlatData.filter(feature => {
            return (feature.name && feature.name.toLowerCase().indexOf(query.toLowerCase()) > -1) || (feature.description && feature.description.toLowerCase().indexOf(query.toLowerCase()) > -1)
        })
    }, [featuresFlatData, query]);

    return !!featuresFlatData.length ? (
        <div className="features-modal">
            <div onClick={() => {dispatch({type: 'SET_IS_DIALOG_OPEN',payload: {isDialogOpen: false}})}}>X</div>
            <div>
                <FeaturesFilters/>
                <FeaturesTable columns={featuresColumns} data={filterFeaturesData}/>
            </div>
        </div> ): (
        <div>Loading...</div>
    )
};

export default FeaturesModal;
