import React, {useCallback, useContext, useEffect, useState, useMemo} from 'react';
import FeaturesTable from '../FeaturesTable/FeaturesTable';
import {fetchFeatures} from '../../lib/mock-api';
import {ICrNode} from "../../lib/types";
import FeaturesFilters from "../FeaturesFilters/FeaturesFilters";
import './FeaturesModal.scss';
import FeaturesContext from "../../FeaturesContext";


const FeaturesModal = () => {
    const [featuresData, setFeaturesData] = useState<ICrNode[]>([]);
    const [featuresColumns] = useState([
        {
            Header: 'NAME',
            accessor: 'name',
        },
        {
            Header: 'VARIABLE ID',
            accessor: 'varId',
        },
        {
            Header: 'DESCRIPTION',
            accessor: 'description',
        },
        {
            Header: 'DOMAINS',
            accessor: 'domains',
        },
        {
            Header: 'LABELS',
            accessor: 'labels',
        },
        {
            Header: 'SOURCES',
            accessor: 'sources',
        },
        {
            Header: 'VALUE',
            accessor: 'value',
        }
    ]);
    const {dispatch, query} = useContext(FeaturesContext);

    const getFeaturesData = useCallback(() => {
        return fetchFeatures();
    }, []);

    useEffect(() => {
        getFeaturesData().then((res) => {
            if(res) {
                setFeaturesData(res.map(item => {
                    return {
                        ...item,
                        ...item.attributes
                    }
                }));
            }
        }).catch((err) => {
            console.log(err)
        });
    }, []);

    useEffect(() => {
        console.log(featuresData)
    }, [featuresData]);

    // const filterFeaturesData = useMemo(() => {
    //     debugger;
    //     console.log(featuresData)
    //     return featuresData.filter(feature => {
    //         return (feature.name.toLocaleLowerCase().indexOf(query.toLowerCase()) > -1) || (feature.description.toLocaleLowerCase().indexOf(query.toLowerCase()) > -1)
    //     })
    // }, [featuresData, query]);

    return (
        <div className="features-modal">
            <div onClick={() => {dispatch({type: 'SET_IS_DIALOG_OPEN',payload: {isDialogOpen: false}})}}>X</div>
            <div>
                <FeaturesFilters/>
                <FeaturesTable columns={featuresColumns} data={featuresData}/>
            </div>
        </div>
    )
};

export default FeaturesModal;
