import React from 'react';
import {useTable, useSortBy, useGroupBy, useFlexLayout, usePagination, useResizeColumns, useRowSelect, CellProps, HeaderProps } from 'react-table';


const FeaturesTable = ({ columns, data } : any) => {

    const selectionHook = (hooks: Hooks<any>) => {
        hooks.allColumns.push((columns: any) => [
            // Let's make a column for selection
            {
                id: '_selector',
                disableResizing: true,
                disableGroupBy: true,
                minWidth: 45,
                width: 45,
                maxWidth: 45,
                // The header can use the table's getToggleAllRowsSelectedProps method
                // to render a checkbox
                Header: ({ getToggleAllRowsSelectedProps }: HeaderProps<any>) => (
                    <div {...getToggleAllRowsSelectedProps()} />
                ),
                // The cell can use the individual row's getToggleRowSelectedProps method
                // to the render a checkbox
                Cell: ({ row }: CellProps<any>) => <div {...row.getToggleRowSelectedProps()} />,
            },
            ...columns,
        ])
        hooks.useInstanceBeforeDimensions.push(({ headerGroups }) => {
            // fix the parent group of the selection button to not be resizable
            const selectionGroupHeader = headerGroups[0].headers[0]
            selectionGroupHeader.canResize = false
        })
    };

    const hooks = [
        useGroupBy,
        useSortBy,
        useFlexLayout,
        usePagination,
        useResizeColumns,
        useRowSelect,
        selectionHook,
    ];

    // Use the state and functions returned from useTable to build your UI
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({
        columns,
        data,
    }, ...hooks);

    // Render the UI for your table
    return (
        <table {...getTableProps()}>
            <thead>
            {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map(column => (
                        <th {...column.getHeaderProps()} {...column.getSortByToggleProps()}>{column.render('Header')}<span>
                    {column.isSorted
                        ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                        : ''}
                  </span></th>
                    ))}
                </tr>
            ))}
            </thead>
            <tbody {...getTableBodyProps()}>
            {rows.map((row, i) => {
                prepareRow(row)
                return (
                    <tr {...row.getRowProps()}>
                        {row.cells.map(cell => {
                            return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                        })}
                    </tr>
                )
            })}
            </tbody>
        </table>
    )
};

export default FeaturesTable;
