import React, {useMemo} from 'react';
import './FeaturesInput.scss';

interface ISelectProps {
    selectName: string,
    selectValue: string,
    selectOnChange: any,
    selectOptions: any[]
}


const FeaturesSelect = ({selectValue, selectName, selectOnChange, selectOptions}: ISelectProps) => {
    return useMemo(() => {
        return (
            <select name={selectName} onChange={selectOnChange}>
                <option value="">{selectValue}</option>
                {selectOptions && selectOptions.map((val, index) => {
                    return <option value={val} key={index}>{val}</option>;
                })}
            </select>
        )
    }, [selectValue, selectName, selectOnChange, selectOptions])
};

export default FeaturesSelect;
