import {NodeTypes} from "./lib/types";

interface IState {
    isDialogOpen: boolean
}

interface IPayload {
    isDialogOpen: boolean
}

interface IAction{
    type: string,
    payload: IPayload
}


export default function FeaturesReducer (state: IState, action: IAction){
    const actionPayload = action.payload;
    switch(action.type) {
        case 'SET_IS_DIALOG_OPEN':
            return {
                ...state,
                isDialogOpen: actionPayload.isDialogOpen
            };

        default:
            return state;
    }
};
