import React, {useCallback, useContext, useMemo} from 'react';
import FeaturesButton from '../FeaturesButton/FeaturesButton';
import FeaturesInput from '../FeaturesInput/FeaturesInput';
import FeaturesContext from "../../FeaturesContext";
import FeaturesSelect from "../FeaturesSelect/FeaturesSelect";

const FeaturesFilters = ({featuresModelMetadata} : any) => {
    const {dispatch, query} = useContext(FeaturesContext);
    const handleDeleteSelected = useCallback(
        ({ state }: TableInstance<T>) => {
            state.selectedRowIds && Object.keys(state.selectedRowIds).length > 0

    }, []);
    const handleSearch = useCallback((event) => {
        dispatch({type: 'SET_SEARCH_QUERY', payload: {query: event.target.value}})
    }, []);

    return useMemo(() => {
        return (
            <div>
                <FeaturesButton onClickButton={handleDeleteSelected} buttonValue="DELETE SELECTED"/>
                <FeaturesInput inputName="search-features" inputType="search" inputOnChange={handleSearch} inputValue={query} inputPlaceholder="Search..."/>
                <FeaturesSelect selectName="filter-by-labels" selectValue="Filter by label" selectOnChange={() => {}} selectOptions={featuresModelMetadata.labels}/>
                <FeaturesSelect selectName="filter-by-domains" selectValue="Filter by domain" selectOnChange={() => {}} selectOptions={featuresModelMetadata.domains}/>
            </div>
        )
    }, [query, featuresModelMetadata])
};

export default FeaturesFilters;
