import React from 'react';
import {useTable, useSortBy, useGroupBy, usePagination, useResizeColumns, useRowSelect, Hooks, Cell, HeaderGroup, CellProps, HeaderProps, Meta} from 'react-table';
import { HeaderCheckbox, RowCheckbox, useStyles } from './FeaturesTableStyles';


const FeaturesTable = ({ columns, data } : any) => {

    const getStyles = <T extends object>(props: any, disableResizing = false, align = 'left') => [
        props,
        {
            style: {
                justifyContent: align === 'right' ? 'flex-end' : 'flex-start',
                alignItems: 'flex-start',
                display: 'flex',
            },
        },
    ]

    // const headerProps = <T extends object>(props: any, { column }: Meta<T, { column: HeaderGroup<T> }>) =>
    //     getStyles(props, column && column.disableResizing, column && column.align);
    //
    // const cellProps = <T extends object>(props: any, { cell }: Meta<T, { cell: Cell<T> }>) =>
    //     getStyles(props, cell.column && cell.column.disableResizing, cell.column && cell.column.align);


    const selectionHook = (hooks: Hooks<any>) => {
        hooks.allColumns.push((columns: any) => [
            // Let's make a column for selection
            {
                id: '_selector',
                disableResizing: true,
                disableGroupBy: true,
                minWidth: 45,
                width: 45,
                maxWidth: 45,
                // The header can use the table's getToggleAllRowsSelectedProps method
                // to render a checkbox
                Header: ({ getToggleAllRowsSelectedProps }: HeaderProps<any>) => (
                    <HeaderCheckbox {...getToggleAllRowsSelectedProps()}  />
                ),
                // The cell can use the individual row's getToggleRowSelectedProps method
                // to the render a checkbox
                Cell: ({ row }: CellProps<any>) => <RowCheckbox {...row.getToggleRowSelectedProps()} />,
            },
            ...columns,
        ])
        hooks.useInstanceBeforeDimensions.push(({ headerGroups }) => {
            // fix the parent group of the selection button to not be resizable
            const selectionGroupHeader = headerGroups[0].headers[0]
            selectionGroupHeader.canResize = false
        })
    };

    const hooks = [
        useGroupBy,
        useSortBy,
        usePagination,
        useResizeColumns,
        useRowSelect,
        selectionHook,
    ];

    // Use the state and functions returned from useTable to build your UI
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({
        columns,
        data,
    }, ...hooks);

    // Render the UI for your table
    return (
        <table {...getTableProps()}>
            <thead>
            {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map(column => (
                        <th {...column.getHeaderProps()} {...column.getSortByToggleProps()}>{column.render('Header')}<span>
                    {column.isSorted
                        ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                        : ''}
                  </span></th>
                    ))}
                </tr>
            ))}
            </thead>
            <tbody {...getTableBodyProps()}>
            {rows.map((row, i) => {
                prepareRow(row)
                return (
                    <tr {...row.getRowProps()}>
                        {row.cells.map(cell => {
                            return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                        })}
                    </tr>
                )
            })}
            </tbody>
        </table>
    )
};

export default FeaturesTable;
