import React, {useEffect, useState} from 'react';
import {useTable, useSortBy, useGroupBy, usePagination, useResizeColumns, useRowSelect, Hooks, CellProps, HeaderProps} from 'react-table';
//import FeaturesTablePagination from './FeaturesTablePagination';
import { HeaderCheckbox, RowCheckbox } from './FeaturesTableStyles';


const FeaturesTable = ({ columns, data, dispatch } : any) => {

    const [currentPage, setCurrentPage] = useState(1);
    const pageNumbers = [];
    const featuresPerPage = 5;
    const indexOfLastFeature = currentPage * featuresPerPage;
    const indexOfFirstFeature = indexOfLastFeature - featuresPerPage;

    for (let i = 1; i <= Math.ceil(data.length / featuresPerPage); i++) {
        pageNumbers.push(i);
    }

    const selectionHook = (hooks: Hooks<any>) => {
        hooks.allColumns.push((columns: any) => [
            {
                id: '_selector',
                disableResizing: true,
                disableGroupBy: true,
                minWidth: 45,
                width: 45,
                maxWidth: 45,
                Header: ({ getToggleAllRowsSelectedProps }: HeaderProps<any>) => {

                    return (
                        <HeaderCheckbox {...getToggleAllRowsSelectedProps()}/>
                    )
                },
                Cell: ({ row }: CellProps<any>) => <RowCheckbox {...row.getToggleRowSelectedProps()} />,
            },
            ...columns,
        ]);
        hooks.useInstanceBeforeDimensions.push(({ headerGroups }) => {
            // fix the parent group of the selection button to not be resizable
            const selectionGroupHeader = headerGroups[0].headers[0]
            selectionGroupHeader.canResize = false
        })
    };

    const hooks = [
        useGroupBy,
        useSortBy,
        usePagination,
        useResizeColumns,
        useRowSelect,
        selectionHook,
    ];

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        state: { pageIndex, pageSize, selectedRowIds },
    } = useTable({
        columns,
        data,
    }, ...hooks);


    useEffect(() => {
        console.log(selectedRowIds, "selectedRowIds");
        dispatch({type: 'SET_SELECTED_ROW_IDS', payload: { selectedRowIds: selectedRowIds }})
    }, [selectedRowIds]);

    return (
        <>
        <table {...getTableProps()}>
            <thead>
            {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map(column => (
                        <th {...column.getHeaderProps()} {...column.getSortByToggleProps()}>{column.render('Header')}<span>
                    {column.isSorted
                        ? column.isSortedDesc
                            ? ' ðŸ”½'
                            : ' ðŸ”¼'
                        : ''}
                  </span></th>
                    ))}
                </tr>
            ))}
            </thead>
            <tbody {...getTableBodyProps()}>
            {rows.map((row, i) => {
                prepareRow(row)
                return (
                    <tr {...row.getRowProps()}>
                        {row.cells.map(cell => {
                            return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                        })}
                    </tr>
                )
            })}
            </tbody>
        </table>
            <ul id="page-numbers">
                {pageNumbers.map(number => {
                    return (
                        <li key={number}>
                            <button id={number} className={number === currentPage ? 'button-current' : 'next-buttons' } onClick={(e) => setCurrentPage(Number(e.target.id))}>{number}</button>
                        </li>
                    );
                })}
            </ul>
        </>
    )
};

export default FeaturesTable;
